import React,{useState} from 'react'
 import "./App.css"
import Search from './Component/Search'
import CurrentWeather from './Component/Current-weather/Current-Weather'
import {WEATHER_API_URL,WEATHER_API_KEY} from "./Component/Api"

const App = () => {

  const[currentWeather,setCurrentWeather] =useState(null);
   const[forecast,setForecast] =useState(null);


  const handleOnSearchChange = (searchData)=>{
    const[lat,lon]=searchData.value.split(" "); 

    const CurrentWeatherFetch=fetch(`${WEATHER_API_URL}/weather?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}`);
    const forecasteFetch=fetch(`${WEATHER_API_URL}/forecast?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}`);
    console.log(CurrentWeatherFetch);
    console.log("$$$$$$$$$$$")

    Promise.all([CurrentWeatherFetch,forecasteFetch])
        .then(async(response)=>{
          const weatherResponse = await response[0].json();
          const forecastResponse = await response[1].json();  
          setCurrentWeather({ city:searchData.label, ...weatherResponse})
          setForecast({ city:searchData.label, ...forecastResponse})
    })
    .catch((err)=>console.log(err));
  }
  console.log(currentWeather);
  console.log(forecast);




  return (
    <>
    <div className='container'>
    <Search onSerachChange={handleOnSearchChange}/>  
    {CurrentWeather && <CurrentWeather data={CurrentWeather}/>}
  
    </div>
    </>
  )
}

export default App
